import React, { useState } from 'react';
import './Home.css';

const Home = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  return (
    <div className="home-container">
      <div className="top-white-space" />
      <div className="home-header">
        <div className="button-group">
          <button className="nav-button" onClick={() => setShowLogin(true)}>Login</button>
          <button className="nav-button" onClick={() => setShowSignup(true)}>Signup</button>
        </div>
      </div>

      <div className="home-main">
        <h1 className="home-title">Welcome to Student Enrollment Portal</h1>
      </div>

      {/* Login Popup */}
      {showLogin && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-btn" onClick={() => setShowLogin(false)}>×</button>
            <h2 className="modal-title">Login</h2>
            <input className="modal-input" type="email" placeholder="Enter your email" />
            <input className="modal-input" type="password" placeholder="Enter your password" />
            <button className="modal-button">Login</button>
          </div>
        </div>
      )}

      {/* Signup Popup */}
      {showSignup && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-btn" onClick={() => setShowSignup(false)}>×</button>
            <h2 className="modal-title">Signup</h2>
            <input className="modal-input" type="email" placeholder="Enter your email" />
            <input className="modal-input" type="password" placeholder="Enter your password" />
            <input className="modal-input" type="password" placeholder="Confirm your password" />
            <button className="modal-button">Signup</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
