// src/Signup.js
import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import { api } from './api';



const Signup = ({ closeModal }) => {
  const navigate = useNavigate(); // <-- initialize

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setStatus("");

    try {
      const res = await fetch("http://localhost:8080/api/auth/signup", { // <-- update backend URL if needed
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        setStatus("Signup successful!");
        closeModal(); // close modal
        navigate("/studentdetails"); // <-- go to StudentDetails page
      } else {
        const data = await res.json();
        setStatus(data.message || "Signup failed.");
      }
    } catch (err) {
      console.error(err);
      setStatus("An error occurred during signup.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={() => closeModal?.()}>Ã—</button>
        <h2>Sign up</h2>
        <form onSubmit={handleSubmit} className="form">
          <div className="field">
            <label>Full name</label>
            <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Jane Doe" required />
          </div>
          <div className="field">
            <label>Email</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" required />
          </div>
          <div className="field">
            <label>Password</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
          </div>
          <div className="field">
            <label>Confirm password</label>
            <input type="password" value={confirm} onChange={(e) => setConfirm(e.target.value)} placeholder="Confirm password" required />
          </div>

          <button type="submit" disabled={busy}>{busy ? 'Signing up...' : 'Create account'}</button>

          {status && <p className="status">{status}</p>}
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
}
