import React, { useState } from 'react';
import Login from './Login';  // Import the Login component

const Home = () => {
  const [showLoginForm, setShowLoginForm] = useState(false); // Control login form visibility
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Track login status

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);  // Handle login success (you can also redirect the user)
    setShowLoginForm(false); // Hide the login form after successful login
  };

  return (
    <div className="page-wrapper">
      {/* White top section with heading */}
      <div className="top-white-space">
        <h1 className="home-title">Welcome to Student Enrollment Portal</h1>
      </div>

      {/* Background image section */}
      <div className="home-container">
        <header className="home-header">
          <div className="button-group">
            {!isLoggedIn && (
              <button className="nav-button" onClick={() => setShowLoginForm(true)}>
                Login
              </button>
            )}
            <button className="nav-button">Signup</button>
          </div>
        </header>

        {/* Display Login form if the Login button is clicked */}
        {showLoginForm && !isLoggedIn && (
          <Login onLoginSuccess={handleLoginSuccess} />
        )}

        {/* Display success message after login */}
        {isLoggedIn && <div className="login-success">Logged in successfully!</div>}
      </div>
    </div>
  );
};

export default Home;
