import React, { useState } from 'react';
import './Home.css';

const Home = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleLoginClick = () => {
    setShowLoginForm(true); // Show the login form when the Login button is clicked
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();

    // Simple validation
    if (!username || !password) {
      setError('Please enter both username and password');
      return;
    }

    setLoading(true); // Set loading state
    setError('');

    // Simulate an API call (you can replace this with an actual API call)
    setTimeout(() => {
      // This is where you would normally check credentials
      if (username === 'admin' && password === 'password') {
        // On success, do something (e.g., redirect to dashboard)
        alert('Login successful');
      } else {
        // On failure, set an error message
        setError('Invalid username or password');
      }
      setLoading(false); // Stop the loading state
    }, 2000);
  };

  return (
    <div className="page-wrapper">
      {/* White top section with heading */}
      <div className="top-white-space">
        <h1 className="home-title">Welcome to Student Enrollment Portal</h1>
      </div>

      {/* Background image section */}
      <div className="home-container">
        <header className="home-header">
          <div className="button-group">
            <button className="nav-button" onClick={handleLoginClick}>
              Login
            </button>
            <button className="nav-button">Signup</button>
          </div>
        </header>

        {/* Show login form when Login button is clicked */}
        {showLoginForm && (
          <div className="login-form">
            <h2>Login</h2>
            <form onSubmit={handleLoginSubmit}>
              <div className="form-group">
                <label htmlFor="username">Username:</label>
                <input
                  type="text"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" disabled={loading}>
                {loading ? 'Logging in...' : 'Login'}
              </button>
              {error && <p className="error">{error}</p>}
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
