        <p>{student.email}</p>
      </header>

      <section className="dashboard-section">
        <h2>ðŸ“š Your Enrolled Courses</h2>
        <div className="courses-list">
          {student.enrolledCourses.map(course => (
            <div className="course-card" key={course.id}>
              <h3>{course.title}</h3>
              <p>Instructor: {course.instructor}</p>
