// Home.js
import React, { useState } from 'react';
import Login from './Login';  // Import the Login component

const Home = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
  };

  return (
    <div className="page-wrapper">
      {/* White top section with heading */}
      <div className="top-white-space">
        <h1 className="home-title">Welcome to Student Enrollment Portal</h1>
      </div>

      {/* Background image section */}
      <div className="home-container">
        <header className="home-header">
          <div className="button-group">
            {!isLoggedIn && (
              <button className="nav-button" onClick={() => setIsLoggedIn(true)}>
                Login
              </button>
            )}
            <button className="nav-button">Signup</button>
          </div>
        </header>

        {/* Show Login Form when Login is clicked */}
        {!isLoggedIn && <Login onLoginSuccess={handleLoginSuccess} />}
      </div>
    </div>
  );
};

export default Home;
