import React, { useState } from 'react';
import './Home.css';
import LoginModal from './LoginModal';  // Import the LoginModal component

const Home = () => {
  const [showLoginModal, setShowLoginModal] = useState(false);  // Control modal visibility

  const handleLoginClick = () => {
    setShowLoginModal(true);  // Show modal when Login button is clicked
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);  // Close modal
  };

  return (
    <div className="page-wrapper">
      {/* White top section with heading */}
      <div className="top-white-space">
        <h1 className="home-title">Welcome to Student Enrollment Portal</h1>
      </div>

      {/* Background image section */}
      <div className="home-container">
        <header className="home-header">
          <div className="button-group">
            <button className="nav-button" onClick={handleLoginClick}>
              Login
            </button>
            <button className="nav-button">Signup</button>
          </div>
        </header>

        {/* Display Login Modal when Login button is clicked */}
        {showLoginModal && <LoginModal closeModal={closeLoginModal} />}
      </div>
    </div>
  );
};

export default Home;
