// src/Signup.js
import React, { useState } from 'react';
import './Signup.css';
import { api } from './api';

export default function Signup({ closeModal }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [error, setError] = useState('');
  const [status, setStatus] = useState('');
  const [busy, setBusy] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();
    setError(''); setStatus('');
    if (password !== confirm) {
      setError('Passwords do not match.');
      return;
    }
    setBusy(true);
    try {
      const res = await api.signup({ name, email, password });
      setStatus(res.message || 'Signup successful');
      setTimeout(() => closeModal?.(), 700);
    } catch (err) {
      setError(err.message);
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={() => closeModal?.()}>Ã—</button>
        <h2>Sign up</h2>
        <form onSubmit={handleSubmit} className="form">
          <div className="field">
            <label>Full name</label>
            <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Jane Doe" required />
          </div>
          <div className="field">
            <label>Email</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="you@example.com" required />
          </div>
          <div className="field">
            <label>Password</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
          </div>
          <div className="field">
            <label>Confirm password</label>
            <input type="password" value={confirm} onChange={(e) => setConfirm(e.target.value)} placeholder="Confirm password" required />
          </div>

          <button type="submit" disabled={busy}>{busy ? 'Signing up...' : 'Create account'}</button>

          {status && <p className="status">{status}</p>}
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
}
