// LoginModal.js
import React, { useState } from 'react';
import './LoginModal.css';  // Import styles for the modal

const LoginModal = ({ closeModal }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleLoginSubmit = (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Please enter both email and password');
      return;
    }

    setLoading(true);
    setError('');

    // Simulate API call for login (replace this with your own API call)
    setTimeout(() => {
      if (email === 'admin@example.com' && password === 'password') {
        alert('Login successful');
        closeModal();  // Close the modal after successful login
      } else {
        setError('Invalid email or password');
      }
      setLoading(false);
    }, 2000);  // Simulated delay
  };

  return (
    <div className="login-modal">
      <div className="modal-content">
        <span className="close-btn" onClick={closeModal}>Ã—</span>
        <h2 className="login-title">Login</h2> {/* Title at the top */}
        <form onSubmit={handleLoginSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email:</label> {/* Label for email */}
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label> {/* Label for password */}
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
          {error && <p className="error">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default LoginModal;
