import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // <-- add this

const Signup = ({ closeModal }) => {
  const navigate = useNavigate(); // <-- initialize

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setStatus("");

    try {
      const res = await fetch("http://localhost:8080/api/auth/signup", { // <-- update backend URL if needed
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (res.ok) {
        setStatus("Signup successful!");
        closeModal(); // close modal
        navigate("/studentdetails"); // <-- go to StudentDetails page
      } else {
        const data = await res.json();
        setStatus(data.message || "Signup failed.");
      }
    } catch (err) {
      console.error(err);
      setStatus("An error occurred during signup.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="close-btn" onClick={closeModal}>Ã—</button>
        <h2>Signup</h2>
        <form className="form" onSubmit={handleSubmit}>
          <div className="field">
            <label>Name</label>
            <input type="text" name="name" value={formData.name} onChange={handleChange} required />
          </div>
          <div className="field">
            <label>Email</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />
          </div>
          <div className="field">
            <label>Password</label>
            <input type="password" name="password" value={formData.password} onChange={handleChange} required />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? "Signing up..." : "Signup"}
          </button>
          {status && <div className={res.ok ? "status" : "error"}>{status}</div>}
        </form>
      </div>
    </div>
  );
};

export default Signup;
